//%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%// open/close Sidenav //%%%%%%%%%%%%%%%5//
$(".opennav").on("click", function (e) {
  e.preventDefault();
  $(".opennav").hide("fast");
  $(".sidenav").show("fast");
  console.log("Opened sidenav");
});
$("#closesidenav").on("click", function (e) {
  e.preventDefault();
  $(".sidenav").hide("fast");
  $("#time").hide("slow");
  $(".opennav").show("fast");
  console.log("Closed sidenav");
});
